mapper.select:
/* board-mapper.xml getItemCount 게시글 갯수 조회 */
SELECT COUNT(*) cnt
  FROM ( ) a

/* board-mapper.xml getItemCount 게시글 갯수 조회 */
select ${total_cnt} as total_cnt
      , ${max_page} as max_page
 FROM (
 ) a
LIMIT ${offset}, ${row_cnt}

mapper.sql:
SELECT tb1.seq , tb1.title , tb1.contents , tb1.author , tb1.category , tb2.cd_nm category_nm , DATE_FORMAT(tb1.reg_dtm, '%Y-%m-%d %H:%i:%s') reg_dtm , DATE_FORMAT(tb1.up_dtm, '%Y-%m-%d %H:%i:%s') up_dtm FROM tb_item tb1 LEFT OUTER JOIN tb_code tb2 ON tb1.category = tb2.cd AND tb2.grp_cd = 'CATEGORY'
AND tb1.seq = #{seq}
AND tb1.category = #{category}
AND UPPER(tb1.title) LIKE CONCAT('%', UPPER(#{title}), '%')
AND UPPER(tb1.contents) LIKE CONCAT('%', UPPER(#{contents}), '%')

mapper.insert:
/* board-mapper.xml insertItem */
INSERT
  INTO tb_item
     (
       title
     , contents
     , author
     , category
     , reg_dtm
     )
VALUES
     (
       #{title}
     , #{contents}
     , #{author}
     , #{category}
     , NOW()
     )

mapper.update:
/* board-mapper.xml updateItem */
UPDATE tb_item
   SET up_dtm = NOW()

     , title = #{title}


    , contents = #{contents}


    , author = #{author}


    , category = #{category}

 WHERE seq = #{seq}

mapper.delete:
/* board-mapper.xml deleteItem */
DELETE FROM tb_item
 WHERE seq = #{seq}
