mapper.sql:
SELECT file_id /* varchar(40) PK comment '파일id' */ , org_file_name /* varchar(256) comment '원 파일이름' */ , file_name /* varchar(256) comment '저장 파일이름' */ , file_path /* varchar(512) comment '파일경로' */ , file_ext /* varchar(10) comment '파일확장자' */ , file_size /* varchar(20) comment '파일크기' */ , in_id /* varchar(50) comment '입력자 id ' */ , DATE_FORMAT(in_dtm, '%Y-%m-%d %H:%i:%s') in_dtm /* datetime comment '입력일시' */ , up_id /* varchar(50) comment '수정자 id' */ , DATE_FORMAT(up_dtm, '%Y-%m-%d %H:%i:%s') up_dtm /* datetime default now() comment '수정일시' */ , system_type FROM tb_file_info tb1
AND tb1.file_id = #{file_id}
AND ( UPPER(tb1.file_id) LIKE CONCAT('%', UPPER(TRIM(#{search_word})), '%') OR UPPER(tb1.org_file_name) LIKE CONCAT('%', UPPER(TRIM(#{search_word})), '%') )
AND tb1.system_type = #{system_type}
ORDER BY in_dtm DESC

mapper.delete:
/* file-mapper.xml deleteFileInfo */
DELETE FROM tb_file_info
 WHERE file_id = #{file_id}
